  font-size: 22px;
  border: none;
  cursor: pointer;
}
.search-container  {
  float: right;
  padding: 15px 15px;
  margin-top: 6px;
  margin-right: 15px;
  background: #3366ff;
  font-size: 22px;
  border: none;
  cursor: pointer;
}


.topnav .search-container button:hover {
  background: #000;
}

@media screen and (max-width: 600px) {
  .topnav .search-container {
    float: none;
  }
  .topnav a, .topnav input[type=text], .topnav .search-container button {
